mapper.update:
update TBLUSER
set USRSTATE = '강퇴'
where USRNUM = #{usrNum}

update CLUB
set CBLEADERNUM = #{usrNum},
CBLEADERNAME = #{usrName}
where CBNUM = #{cbNum}

update userauth
set  auth = 'ROLE_MEMBEROUT'
where  USRNUM = #{usrNum}

update notice
set ntTitle=#{ntTitle},ntDate= TO_CHAR(sysdate,'YYYY-MM-DD HH24:MI:SS')
where ntNum = #{ntNum}

update noticeContent
set ntContent=#{ntContent} 
where ntNum=#{ntNum}
