mapper.update:
update userauth
set  auth = 'ROLE_MEMBEROUT'
where  USRNUM = #{usrNum}

update CLUBMEMBER
set  CBMBSTRESULT = #{cbMbStResult}
where  USRNUM = #{usrNum} and CBNUM = #{cbNum}

update CLUB
set CBCURMBNUM = CBCURMBNUM + 1
where CBNUM = #{cbNum}

update CLUB
set CBCURMBNUM = CBCURMBNUM - 1
where CBNUM = #{cbNum}
