mapper.insert:
select club_seq.nextval FROM DUAL
{ CALL begin insert into club (cbNum, cbIntro, cbName, cbCategory, cbMbnum, cbSubcat, cbHashtag ,cbCity, cbDistrict, cbType, cbLeaderNum ,cbLeaderName,cbCurMbnum) values(#{cbNum}, #{cbIntro}, #{cbName}, #{cbCategory}, #{cbMbnum}, #{cbSubcat}, #{cbHashtag}, #{cbCity}, #{cbDistrict}, '번개모임', #{cbLeaderNum}, #{cbLeaderName}, 1);
insert into thunderDetail (cbNum,cbDate, cbAppPeriod, cbPlace, cbSupplies) values(#{cbNum}, #{thunderDetailVO.cbDate}, #{thunderDetailVO.cbAppPeriod}, #{thunderDetailVO.cbPlace}, #{thunderDetailVO.cbSupplies});
END }

mapper.delete:
{
CALL
begin
delete from thunderDetail where cbNum= #{cbNum};

delete from club where cbNum= #{cbNum};
END
}

mapper.update:
{
CALL
begin

update club set cbIntro=#{cbIntro},
cbName=#{cbName},
cbCategory=#{cbCategory},
cbMbnum=#{cbMbnum},
cbSubcat=#{cbSubcat},
cbHashtag=#{cbHashtag}, cbCity=#{cbCity},
cbDistrict=#{cbDistrict}
where cbNum = #{cbNum};


update thunderDetail
set cbDate=#{thunderDetailVO.cbDate},
cbAppPeriod=#{thunderDetailVO.cbAppPeriod},
cbPlace=#{thunderDetailVO.cbPlace},
cbSupplies=#{thunderDetailVO.cbSupplies}
where cbNum = #{cbNum};

END
}
